import React,{Component} from 'react';
import './App.css';
import Food from'./Food';
import Snake from './Snake';

const getRandomCoordinates = () => {
  let min = 1;
  let max = 98;
  let x = Math.floor((Math.random()*(max-min+1)+min)/2)*2;
  let y =  Math.floor((Math.random()*(max-min+1)+min)/2)*2;
  return [x,y]
}

const initialState = {
  food: getRandomCoordinates(),
  speed: 100,
  direction: 'RIGHT',
  snakeDots: [
    [0,0],
    [2,0]
  ],
  play : false,
}

class App extends Component {
  constructor() {
    this.move = setInterval(this.moveSnake,this.state.speed)
  }
  state = initialState;
  componentDidMount() {
    document.onkeydown = this.onKeyDown;
  }

  componentDidUpdate() {
    this.checkIfOutOfBorders();
    this.checkIfCollapsed();
    this.checkIfEat();
  }


  onKeyDown = (e) => {
    e = e || window.event;
    if(this.state.direction === 'UP')
      {
        switch (e.keyCode) {
        case 38:
          this.setState({direction: 'UP'});
          break;
        case 37:
          this.setState({direction: 'LEFT'});
          break;
        case 39:
          this.setState({direction: 'RIGHT'});
          break;
        default:
          break;
      }
    }
  if(this.state.direction === 'DOWN')
    {
      switch (e.keyCode) {
      case 40:
        this.setState({direction: 'DOWN'});
        break;
      case 37:
        this.setState({direction: 'LEFT'});
        break;
      case 39:
        this.setState({direction: 'RIGHT'});
        break;
      default:
        break;
    }
  }
  if(this.state.direction === 'LEFT')
    {
      switch (e.keyCode) {
      case 38:
        this.setState({direction: 'UP'});
        break;
      case 40:
        this.setState({direction: 'DOWN'});
        break;
      case 37:
        this.setState({direction: 'LEFT'});
        break;
      default:
        break;
    }
  }
  if(this.state.direction === 'RIGHT')
    {
      switch (e.keyCode) {
      case 38:
        this.setState({direction: 'UP'});
        break;
      case 40:
        this.setState({direction: 'DOWN'});
        break;
      case 39:
        this.setState({direction: 'RIGHT'});
        break;
      default:
        break;
    }
  }
    }
  moveSnake = () => {
    let dots = [...this.state.snakeDots];
    let head = dots[dots.length - 1];
      switch (this.state.direction) {
        case 'RIGHT':
          head = [head[0] + 2, head[1]];
          break;
        case 'DOWN':
          head = [head[0], head[1] + 2];
          break;
        case 'UP':
          head = [head[0], head[1] - 2];
          break;
        case 'LEFT':
          head = [head[0] - 2, head[1]];
          break;
        default:
          break;
        }
    dots.push(head);
    dots.shift();
    this.setState({
      snakeDots: dots
    })
  }
  checkIfOutOfBorders() {
    let head = this.state.snakeDots[this.state.snakeDots.length - 1];
    if (head[0] >= 100 || head[1] >= 100 || head[0] < 0 || head[1] < 0) {
      this.onGameOver();
    }
  }
  checkIfCollapsed() {
    let snake = [...this.state.snakeDots];
    let head = snake[snake.length - 1];
    snake.pop();
    snake.forEach(dot => {
      if (head[0] === dot[0] && head[1] === dot[1]) {
        this.onGameOver();
      }
    })
  }
  checkIfEat() {
    let head = this.state.snakeDots[this.state.snakeDots.length - 1];
    let food = this.state.food;
    if (head[0] === food[0] && head[1] === food[1]) {
      this.setState({
        food: getRandomCoordinates()
      })
      this.enlargeSnake();
      this.increaseSpeed();
    }
  }
  enlargeSnake() {
    let newSnake = [...this.state.snakeDots];
    newSnake.unshift([])
    this.setState({
      snakeDots: newSnake
    })
  }
  increaseSpeed() {
    if (this.state.speed > 20) {
      this.setState({
        speed: this.state.speed - 20
      })
    }
    else{
      this.setState({
        speed: this.state.speed - 2
      })
    }
  }
  onGameOver() {
    this.setState(initialState);
    }
  ToggleChangeMode(){
    this.setState({play : true});
    var move = setInterval(this.moveSnake, this.state.speed)
  }
   ButtonPlay = () => {
    if(!this.state.play){
    return(
      <div>
        <button onClick={this.ToggleChangeMode.bind(this)} > Play </button>
      </div>
    );
  };
  }
  render(){
    return (
      <div>
        <div className="game-area">
          <Snake snakeDots={this.state.snakeDots}/>
          <Food dot={this.state.food}/>
        </div>
        {this.ButtonPlay()}
      </div>

    );
  }
}

export default App;
