          head = [head[0] + 2, head[1]];
          dots.push(head);
          dots.shift();
          this.setState({
            snakeDots: dots
          })
          break;
        case 'DOWN':
          head = [head[0], head[1] + 2];
          dots.push(head);
          dots.shift();
          this.setState({
