    if(!this.state.play){
    return(
      <div>
        <Button onClick={this.ToggleChangeMode.bind(this)} > Play </Button>
      </div>
    );
  };
  }
  TrapStart = () =>{
    if(this.state.snakeDots.length >= 4)
    {
      return (
        <div>
          <Trap trap={this.state.trap} />
        </div>
      );
    }
  };

  render(){
    return (
      <Container>
        <Grid container  spacing={2} >
          <div className='score'>
            <Typography variant="h5" component="h5" className='fontScore' >
              Score : {this.state.snakeDots.length -2 }
            </Typography>
          </div>
          <div className="game-area">
            <Snake snakeDots={this.state.snakeDots}/>
            <Food dot={this.state.food}/>
            {this.TrapStart()}
          </div>
        </Grid>
        {this.ButtonPlay()}
        <Popup />

      </Container>

    );
  }
}

export default App;
