      case 38:
        this.setState({direction: 'UP'});
        break;
      case 40:
        this.setState({direction: 'DOWN'});
        break;
      default:
        break;
    }
  }
  if(this.state.direction === 'RIGHT')
    {
      switch (e.keyCode) {
      case 38:
        this.setState({direction: 'UP'});
        break;
      case 40:
        this.setState({direction: 'DOWN'});
        break;
      default:
        break;
    }
  }
    }
