    let dots = [...this.state.snakeDots];
    let head = dots[dots.length - 1];
      switch (this.state.direction) {
        case 'RIGHT':
          head = [head[0] + 2, head[1]];
          dots.push(head);
          dots.shift();
          this.setState({
            snakeDots: dots
          })
          break;
        case 'DOWN':
          head = [head[0], head[1] + 2];
          dots.push(head);
          dots.shift();
          this.setState({
            snakeDots: dots
          })
          break;
        case 'UP':
          head = [head[0], head[1] - 2];
          dots.push(head);
          dots.shift();
          this.setState({
            snakeDots: dots
          })
          break;
        case 'LEFT':
          head = [head[0] - 2, head[1]];
          dots.push(head);
          dots.shift();
          this.setState({
            snakeDots: dots
          })
          break;
        default:
          break;
        }

  }
  checkIfOutOfBorders() {
    let head = this.state.snakeDots[this.state.snakeDots.length - 1];
    if (head[0] === 100 || head[1] === 100 || head[0] < 0 || head[1] < 0) {
      this.onGameOver();
    }
  }
   checkIfCollapsed() {
    let snake = [...this.state.snakeDots];
    let head = snake[snake.length - 1];
    snake.pop();
    snake.map(dot=>{
      if(head[0]===dot[0] && head[1]===dot[1])
        this.onGameOver();
    })
  }
  checkIfEat() {
    let head = this.state.snakeDots[this.state.snakeDots.length - 1];
    let food = this.state.food;
    if (head[0] === food[0] && head[1] === food[1]) {
